interface userAuthProps {
  token: string
  username: string
  password: string
  address?: string
  nickname?: string
  tel?: string
  email?: string
  gender?: 'M' | 'F'
}

const userState: userAuthProps = {
  token: '',
  username: '',
  password: '',
}
//함수 인터페이스
interface loginOrRegisterProps {
  username: string
  password: string
}

interface setProfileProps {
  address: string
  nickname: string
  tel: string
  email: string
  gender: 'M' | 'F'
}

// 액션 타입
const LOGIN = 'user/LOGIN'
const REGISTER = 'user/REGISTER'
const SET_PROFILE = 'user/SET_PROFILE'
const LOGOUT = 'user/LOGOUT'

//액션 생성함수
export const login = ({ username, password }: loginOrRegisterProps) => ({
  type: 'LOGIN',
  username,
  password,
})

export const register = ({ username, password }: loginOrRegisterProps) => ({
  type: 'REGISTER',
  username,
  password,
})

export const setProfile = ({
  address,
  nickname,
  tel,
  email,
  gender,
}: setProfileProps) => ({
  type: 'SET_PROFILE',
  body: {
    address,
    nickname,
    tel,
    email,
    gender,
  },
})

function userAuth(state: userAuthProps = userState, action) {
  switch (action.type) {
    case 'LOGIN':
      return
    case 'REGISTER':
      return
    case 'SET_PROFILE':
      return
    case 'LOGOUT':
      return
    default:
      return state
  }
}
export default userAuth
